-- Curated FCC Overrides (27 entries)
DROP TABLE IF EXISTS curated_overrides;
CREATE TABLE curated_overrides (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    fcc_id TEXT NOT NULL,
    make TEXT NOT NULL,
    model TEXT,
    year_start INTEGER,
    year_end INTEGER,
    frequency TEXT,
    chip TEXT,
    key_blank TEXT,
    programmer TEXT,
    immo_system TEXT,
    notes TEXT,
    amazon_url TEXT,
    source TEXT DEFAULT 'manual'
);

