INSERT OR REPLACE INTO vehicle_descriptions (vehicle_key, description, generated) VALUES ('Honda|Element', 'The Honda Element utilizes the Megamos ID48 or Philips ID46 transponder systems throughout its production life. Programming is a standard Honda OBDII task that is well-supported by all professional diagnostic tools, typically taking less than 5 minutes. The mechanical ignition is a very common failure point on the Element; if the key won''t turn, expect a worn wafer or collapsed tumblers. Origination of keys is simple using a Lishi HO01 tool to decode the door or ignition locks.', '2026-02-05');
