INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PATS Type A', 'Ford PATS Type A', '', 'immobilizer', '', 'Ford''s earliest Passive Anti-Theft System implementation uses a standalone PATS control module separate from other vehicle systems. It requires only one programmed key to add additional keys and supports up to 16 keys total with basic transponder encryption. Programming is straightforward and widely supported by aftermarket tools like FORScan and basic key programmers.', 1996, 1998, '', '[]', '["Mustang", "Taurus/Sable", "Contour/Mystique", "Mark VIII"]', '[]', '{}', '', '', NULL, '["SecuriLock Type A", "PATS I"]', '', 'https://www.therangerstation.com/tech/understanding-the-ford-pats-passive-anti-theft-system');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PATS Type B', 'Ford PATS Type B', '', 'immobilizer', '', 'An evolution of Type A, this system also uses a standalone PATS module but adds an anti-scan feature and requires two programmed keys to add more, limiting to 8 keys total. It employs improved encrypted transponder communication for better security. Aftermarket support is strong with tools like Autel, Xhorse, and FORScan capable of key programming and parameter resets.', 1998, 2002, '', '[]', '["Explorer/Mountaineer", "Ranger (V6)", "Expedition", "Crown Victoria/Grand Marquis"]', '[]', '{}', '', '', NULL, '["SecuriLock Type B"]', '', 'https://www.therangerstation.com/tech/understanding-the-ford-pats-passive-anti-theft-system');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PATS Type C', 'Ford PATS Type C', '', 'immobilizer', '', 'This variant integrates the PATS control function into the instrument cluster (VIC/HEC/ICM) rather than a standalone module. It follows Type B programming rules with a minimum of two keys required and a maximum of eight, using encrypted transponders. Parameter resets are needed for certain repairs, supported by professional aftermarket tools.', 1999, NULL, '', '[]', '["F-150", "Mustang", "Town Car", "Expedition", "Focus", "Escape"]', '[]', '{}', '', '', NULL, '["SecuriLock Type C"]', '', 'https://www.therangerstation.com/tech/understanding-the-ford-pats-passive-anti-theft-system');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PATS Type D', 'Ford PATS Type D', '', 'immobilizer', '', 'A rare variant with the PATS module integrated into the steering column ignition lock or as a standalone unit behind the dash on the left side. It follows Type A programming with one key minimum and up to 16 keys supported using basic encryption. Widely compatible with aftermarket programming tools due to simpler requirements.', 1997, 1998, '', '[]', '["Continental", "Mark VIII (select)"]', '[]', '{}', '', '', NULL, '["SecuriLock Type D"]', '', 'https://www.therangerstation.com/tech/understanding-the-ford-pats-passive-anti-theft-system ; https://ricksfreeautorepairadvice.com/ford-pats-antitheft-system');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PATS Type E', 'Ford PATS Type E', '', 'immobilizer', '', 'The PATS control function is fully integrated into the Powertrain Control Module (PCM), eliminating a separate module. Programming allows one key minimum with up to 16 supported, but requires specific key code erase procedures for replacements. Advanced encrypted transponders are used, with aftermarket support from high-end tools requiring authentication.', 2000, 2007, '', '[]', '["Ranger (4/6 cyl)", "Explorer", "Escape", "Focus", "Crown Victoria"]', '[]', '{}', '', '', NULL, '["SecuriLock Type E", "E-PATS"]', '', 'https://www.therangerstation.com/tech/understanding-the-ford-pats-passive-anti-theft-system ; https://my.cardone.com/techdocs/PT%2078-0004.pdf');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PATS Type F', 'Ford PATS Type F', '', 'immobilizer', '', 'A later variant often grouped with advanced systems requiring parameter reset procedures similar to Types B/C/G for PCM-related work. It uses stronger encryption and integration typical of mid-2000s platforms, sometimes in hybrid or specific models. Professional authenticated tools are required for key programming and diagnostics.', 2006, 2010, '', '[]', '["Escape Hybrid", "Select modular platforms"]', '[]', '{}', '', '', NULL, '["SecuriLock Type F"]', '', 'https://www.therangerstation.com/tech/understanding-the-ford-pats-passive-anti-theft-system ; https://my.cardone.com/techdocs/PT%2078-0004.pdf');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('SGW', 'Secure Gateway Module', 'Security Gateway Module', 'architecture', '', 'The Secure Gateway Module (SGW) is a security component introduced by FCA (now Stellantis) starting in select 2018 models and widely implemented from 2019 onward. It acts as a firewall between the public CAN network, including the OBD-II diagnostic port, and the vehicle''s private networks to prevent unauthorized access and exploitation. This restriction limits aftermarket diagnostic tools from performing advanced functions such as module programming, key learning, and bi-directional control without authenticated access or a physical bypass.', 2018, NULL, '', '[]', '["Wrangler JL (2018+)", "Gladiator JT (2020+)", "Ram 1500 DT (2019+)", "Grand Cherokee WL (2021+)", "most other FCA/Stellantis vehicles 2018+"]', '[]', '{}', '', 'Read commands pass through; Write commands blocked without valid certificate. Integrated into BCM on 2024+ Stellantis ''Atlantis'' platform.', NULL, '["Security Gateway Module", "SGM", "Secure Gateway", "FCA Security Gateway"]', 'Read commands pass through; Write commands blocked without valid certificate. Integrated into BCM on 2024+ Stellantis ''Atlantis'' platform.', 'https://jscan.net/fca-security-gateway-module-basic-info-and-location');
