INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('KVM', 'Keyless Vehicle Module', '', 'immobilizer', '', 'The Keyless Vehicle Module (KVM) is the primary security and immobilizer module in Jaguar Land Rover vehicles, responsible for keyless entry, passive start, smart key authentication, remote functions, and anti-theft protection. It integrates with the vehicle''s body control system and was used extensively from approximately 2010 to 2018 before being largely succeeded by the Remote Function Actuator (RFA) in newer models. Key programming on KVM-equipped vehicles often requires bench access or specialized tools due to security restrictions.', 2010, 2018, '', '[]', '["Range Rover (L405, 2013-2018)", "Range Rover Sport (L494, 2014-2018)", "Range Rover Evoque (L538, 2012-2018)", "Range Rover Velar (2017-2018)", "Discovery Sport (L550, 2015-2018)", "Discovery 5 (2017-2018)", "Jaguar F-PACE (2016-2018)", "Jaguar F-TYPE (2014-2018)", "Jaguar XE/XF/XJ (2016-2018)"]', '[]', '{}', '', '', NULL, '["Key Vehicle Module", "JLR KVM"]', '', 'https://abrites.com/blog/the-ultimate-guide-to-modern-jaguar-land-rover-key-programming-from-an-abrites-perspective');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('NATS 1', 'Nissan Anti-Theft System Generation 1', '', 'immobilizer', '', 'The earliest NATS immobilizer generation, introduced in the mid-1990s primarily on non-US market models. It uses basic fixed-code transponder chips without cryptographic encryption. Key registration is performed directly via dealer diagnostic tools without a standardized PIN code requirement.', 1995, 1998, '', '[]', '["Primera", "Maxima (select early)", "300ZX (select)"]', '[]', '{}', '', '', NULL, '["Early NATS"]', '', 'Aftermarket locksmith resources and historical Nissan documentation');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('NATS 2', 'Nissan Anti-Theft System Generation 2', '', 'immobilizer', '', 'Expanded NATS implementation from late 1990s to early 2000s, including early US models like Infiniti I30. Typically uses Philips ID33 or basic Texas Instruments fixed-code transponders. Key programming is tool-based with limited or no 4-digit PIN requirement.', 1998, 2002, '', '[]', '["Micra", "Almera", "Sentra (early)", "Infiniti I30", "Maxima"]', '[]', '{}', '', '', NULL, '["NATS V2.0"]', '', 'Aftermarket locksmith resources and Nissan service introductions');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('NATS 4', 'Nissan Anti-Theft System Generation 4', '', 'immobilizer', '', 'Transitional generation used in early to mid-2000s models, introducing ID46 transponders (often non-crypto or early crypto variants). Some models require basic PIN or encrypted data for programming. Serves as a bridge to full cryptographic systems.', 2000, 2005, '', '[]', '["350Z (early)", "Murano (early)", "Altima (early 2000s)", "G35 (early)"]', '[]', '{}', '', '', NULL, '["Pre-NATS 5", "Type 4 Immobilizer"]', '', 'https://automotivetechinfo.com/wp-content/uploads/2009/12/An-Introduction-to-Nissan-Anti-Theft-Systems.pdf');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('NATS 5', 'Nissan Anti-Theft System Generation 5', '', 'immobilizer', '', 'Major cryptographic generation starting mid-2000s, using Philips Crypto ID46 (Hitag2/PCF7936) or equivalent transponders. Requires a 4-digit PIN code for key programming, calculated from encrypted codes and date on the ICU/IMMU label (STEP-5). Common on many US-market vehicles from 2003+.', 2003, 2012, '', '[]', '["350Z", "Altima 2005+", "Maxima 2004+", "Murano 2003+", "Pathfinder 2004+", "Frontier 2004+", "G35", "FX35"]', '[]', '{}', '', '', NULL, '["NATS 5.0", "NVIS (US)"]', '', 'https://nissan-techinfo.locksmithsdrm.com/sdrm/ni_02_4218/Overview%20of%20the%20SDRM%20process.pdf');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('NATS 5.6', 'Nissan Anti-Theft System Generation 5.6', '', 'immobilizer', '', 'Variant of NATS 5 with minor updates, identified by STEP-5.6 on ICU label. Uses the same Philips Crypto ID46 transponders as NATS 5. PIN code is calculated similarly from ICU label encrypted data and date code using dedicated calculators.', 2005, 2012, '', '[]', '["Rogue (early)", "Versa", "Sentra (mid-2000s)", "Tiida", "Note"]', '[]', '{}', '', '', NULL, '["NATS 5 variant"]', '', 'https://www.weboctopus.nl/nissan-infiniti-NATS.php');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('NATS 6', 'Nissan Anti-Theft System Generation 6', '', 'immobilizer', '', 'Advanced generation for late 2000s+ models, often with Intelligent Key systems. Uses Philips Crypto ID46/Hitag2 transponders with rolling codes in proximity keys. PIN code calculated from SEC label (STEP-6) encrypted data using specialized tools.', 2007, NULL, '', '[]', '["370Z", "Murano (later)", "Altima (later)", "Rogue 2008+", "GT-R", "QX56", "M series"]', '[]', '{}', '', '', NULL, '["NATS 6.0"]', '', 'https://www.weboctopus.nl/nissan-infiniti-NATS.php');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('SPA', 'Scalable Product Architecture', '', 'platform', '', 'Volvo''s modular platform introduced in 2014 with the second-generation XC90, designed for mid-size and large vehicles in the 60- and 90-series. It supports internal combustion, hybrid, and later pure electric powertrains through evolutions like SPA2 (debuted 2023 on EX90 and Polestar 3). Over four million vehicles have been produced on the original SPA architecture as of 2025.', 2014, NULL, '', '[]', '["XC90", "S90", "V90", "XC60", "S60", "V60", "EX90", "Polestar 1", "Polestar 3"]', '[]', '{}', '', '', NULL, '["Volvo SPA", "SPA platform", "SPA2"]', '', 'https://en.wikipedia.org/wiki/Scalable_Product_Architecture_platform');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('CMA', 'Compact Modular Architecture', '', 'platform', '', 'Compact modular platform jointly developed by Volvo and Geely, first launched in 2017 with the XC40 compact SUV. It supports front- or all-wheel drive layouts and a wide range of powertrains including full electric, primarily used for Volvo''s 40-series vehicles and related models from Polestar and Lynk & Co.', 2017, NULL, '', '[]', '["XC40", "EX40", "C40", "EC40", "Polestar 2"]', '[]', '{}', '', '', NULL, '["Volvo CMA", "CMA platform"]', '', 'https://en.wikipedia.org/wiki/Compact_Modular_Architecture');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('VIDA', 'Vehicle Information & Diagnostics for Aftersales', '', 'tool', '', 'Official Volvo dealer-level diagnostic and programming software used for vehicle configuration, software updates, and key programming on 2016-2026 models. It requires a paid subscription and a compatible J2534 pass-thru interface for SPA and CMA platforms, as the older DiCE unit is not supported on these architectures.', 2010, NULL, '', '[]', '[]', '[]', '{}', '', '', NULL, '["Volvo VIDA", "VIDA subscription"]', '', 'https://www.volvotechinfo.com/vida');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('DiCE', 'Diagnostic Communication Equipment', '', 'tool', '', 'Volvo''s proprietary diagnostic interface used with VIDA software for older platforms up to approximately 2014-2016. It is not compatible with SPA and CMA platform vehicles introduced from 2015 onward, requiring J2534 pass-thru devices instead for diagnostics and programming.', 2005, 2016, '', '[]', '[]', '[]', '{}', '', '', NULL, '["Volvo DiCE", "DiCE interface"]', '', 'https://www.volvotechinfo.com/vida');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('SGP', 'Subaru Global Platform', '', 'platform', '', 'The Subaru Global Platform (SGP) is a modular unibody architecture introduced by Subaru in 2016 and first implemented in the fifth-generation Impreza for the 2017 model year. It significantly increases torsional rigidity (70-100% improvement depending on model), enhances crash energy absorption, and improves handling and ride comfort through optimized suspension geometry and lower center of gravity.', 2016, NULL, '', '[]', '["Impreza (2017\u2013present)", "Crosstrek/XV (2018\u2013present)", "Forester (2019\u2013present)", "Ascent (2019\u2013present)", "Legacy (2020\u2013present)", "Outback (2020\u2013present)", "WRX (2022\u2013present)", "Solterra (2023\u2013present, e-SGP variant)"]', '[]', '{}', '', '', NULL, '["Subaru Global Platform"]', '', 'https://en.wikipedia.org/wiki/Subaru');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('Type H Immobilizer', 'Subaru Type H Immobilizer', '', 'immobilizer', '', 'The Type H immobilizer is Subaru''s transponder-based anti-theft system used in keyless push-button start models starting around 2019-2020. It requires secure server authentication through Subaru''s dealer network for key registration and immobilizer reprogramming, preventing unauthorized local programming.', 2019, NULL, '', '[]', '["Legacy (2020+)", "Outback (2020+)", "Ascent (2019+)", "Forester (certain 2020+ trims)"]', '[]', '{}', '', '', NULL, '["H-Type Immobilizer", "Server-Authenticated Immobilizer"]', '', 'Subaru Service Information Bulletins');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('TS-10', 'Tesla Access System', '', 'platform', '', 'Tesla''s modern vehicle access system primarily relies on phone keys using Bluetooth Low Energy (BLE) for seamless walk-up unlock and drive authentication, with passive NFC key cards serving as a reliable backup. It employs advanced encrypted challenge-response protocols based on ECDH to ensure security, making unauthorized cloning of key cards effectively impossible.', 2017, NULL, '', '[]', '["Model 3", "Model Y", "Model S (2021+)", "Model X (2021+)", "Cybertruck"]', '[]', '{}', '', '', NULL, '["Tesla Phone Key", "Tesla Key Card", "Tesla BLE/NFC Authentication"]', '', 'https://www.tesla.com/support/tesla-vehicle-keys');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('SFD', 'Schutz der Fahrzeugdiagnose', '', 'immobilizer', '', 'SFD is a Volkswagen Group security gateway that protects diagnostic access to critical vehicle systems, including immobilizer and key programming functions. Introduced progressively from around 2018 and widespread by 2020, it requires authenticated unlocking to perform advanced operations on protected modules.', 2018, NULL, '', '[]', '["Most Porsche models 2020-present on VAG platforms"]', '[]', '{}', '', '', NULL, '["Security Gateway", "Vehicle Diagnostic Protection"]', '', 'https://wiki.ross-tech.com/wiki/index.php/SFD');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PIWIS', 'Porsche Integrated Workshop Information System', '', 'tool', '', 'PIWIS is Porsche''s official dealer-level diagnostic and programming system for full vehicle access, including immobilizer adaptation, key learning, ECU coding, and software updates. Current versions are PIWIS 3 and PIWIS 4, with official use requiring Porsche accreditation and online validation.', 2010, NULL, '', '[]', '["All Porsche models 1996-present"]', '[]', '{}', '', '', NULL, '["PIWIS Tester", "PIWIS III", "PIWIS IV"]', '', 'Porsche official documentation');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('BSI Rolling Code', 'Honda/Acura BSI Rolling Code', '', 'immobilizer', '', 'The Honda/Acura BSI rolling code system is an enhanced security feature integrated into the Body Control Module (BCM) that uses dynamic rolling codes for smart key fob authentication and immobilizer synchronization. Incorrect or interrupted programming attempts with incompatible aftermarket tools can trigger a protective lockout, permanently bricking the BCM and requiring replacement.', 2021, NULL, '', '[]', '["Accord", "Civic", "CR-V", "Pilot", "Odyssey", "Ridgeline", "MDX", "RDX", "TLX"]', '[]', '{}', '', '', NULL, '["Honda BCM Rolling Code", "Honda Smart Key Rolling Code Security"]', '', 'https://static.nhtsa.gov/odi/tsbs/2022/MC-10228193-0001.pdf');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('BP', 'BP Platform', '', 'platform', '', 'The BP platform is the fourth-generation chassis introduced with the 2019 Mazda3 and extended to models like the CX-30 from 2020 onward. It incorporates Mazda''s Skyactiv-Vehicle Architecture with improved structural rigidity and optional all-wheel drive. Newer proprietary immobilizer systems are standard on BP-based vehicles.', 2019, NULL, '', '[]', '["Mazda3", "CX-30", "Mazda CX-50 (partial share)"]', '[]', '{}', '', '', NULL, '["Fourth Generation Mazda3 Platform", "Skyactiv-Vehicle Architecture (Gen 2)"]', '', 'https://en.wikipedia.org/wiki/Mazda3#Fourth_generation_(BP;_2019)');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('MDARS', 'Mazda Diagnostic and Repair Software', '', 'tool', '', 'MDARS is Mazda''s official cloud-based dealer diagnostic and programming software introduced for seventh-generation vehicles starting with the 2019 Mazda3 and 2020 CX-30. It supports full diagnostics, module reprogramming, and immobilizer/key programming. A subscription and compatible VCI interface are required.', 2019, NULL, '', '[]', '["Mazda3 (2019+)", "CX-30 (2020+)", "CX-90", "CX-70"]', '[]', '{}', '', '', NULL, '["Mazda MDARS"]', '', 'https://www.mazdaserviceinfo.com/mdars-software');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('7-minute security wait', '7-Minute Security Delay', '', 'immobilizer', '', 'The 7-minute security wait is a timed anti-brute-force delay enforced during key programming on BP-platform Mazda vehicles like the 2019+ Mazda3. After initiating the procedure, the system requires waiting up to 7 minutes for a horn confirmation signal before proceeding with key registration.', 2019, NULL, '', '[]', '["Mazda3 (BP)", "CX-30"]', '[]', '{}', '', '', NULL, '["Programming horn wait", "Security access timeout"]', '', 'Locksmith tool procedures (Autel/Abrites)');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('BA', 'Toyota BA Smart Key System', '', 'immobilizer', '', 'The Toyota BA (also called BABA or BA immo type) is a smart key immobilizer system used in newer Toyota and Lexus vehicles with Tokai Rika proximity technology. It presents challenges for direct-connect programming and all-keys-lost scenarios, requiring specialized adapters like Xhorse BA AKL or specific tool updates.', 2021, NULL, '', '[]', '["2022+ RAV4", "Camry", "Corolla", "Highlander"]', '[]', '{}', '', '', NULL, '["BABA", "BA immo", "Type BA", "Toyota BABA system"]', '', 'http://blog.xhorsetool.com/how-to-add-smart-key-with-xhorse-toyota-ba-akl-cable');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('Anti-Theft Software Update', 'Hyundai/Kia Immobilizer Software Upgrade', '', 'immobilizer', '', 'The Hyundai and Kia anti-theft software upgrade, released starting in 2023, adds immobilizer-like logic to millions of vehicles previously lacking factory immobilizers to counter viral theft methods. It extends alarm duration, requires key fob presence for starting, and significantly reduced whole-vehicle theft rates.', 2023, NULL, '', '[]', '["2011-2022 Elantra", "Sonata", "Soul", "Sportage"]', '[]', '{}', '', '', NULL, '["Kia Boyz fix", "Hyundai/Kia theft prevention update"]', '', 'https://owners.hyundaiusa.com/us/en/resources/technology-and-navigation/anti-theft-software-upgrade');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('Active Alarm', 'Ford Active Alarm (Security Alarm Activation)', '', 'immobilizer', '', 'In 2021+ Ford vehicles like the F-150, the active alarm system triggers during key programming, especially in all-keys-lost scenarios, causing the horn to sound continuously and potentially interrupting the process. Locksmiths must use specific tools or accessories to manage or bypass the alarm activation during security access.', 2021, NULL, '', '[]', '["F-150", "Ranger", "Other similar platforms"]', '[]', '{}', '', '', NULL, '["Alarm activation during programming", "Security access alarm"]', '', 'https://www.youtube.com/watch?v=ZgRVjtMG5hY');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('PEPS', 'PEPS (Passive Entry Passive Start)', '', 'platform', '', 'PEPS is the smart key system used in newer Toyota vehicles, including the 2024 Tundra, relying on a smart box for key validation and passive functions. Programming issues occur if the PEPS smart box has been reset or contains invalid data, leading to ''no valid key data'' errors.', 2022, NULL, '', '[]', '["Tundra", "Tacoma", "Other 3rd-gen platforms"]', '[]', '{}', '', '', NULL, '["Passive Entry Passive Start", "Smart Key System", "PEPS Smart Box"]', '', 'http://www.lonsdor.org/lonsdor-k518-pro-2024-toyota-tundra-no-valid-key-data-solution');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('MQB49 5C/5D', 'VAG MQB49 5C/5D Immobilizer', '', 'immobilizer', '', 'MQB49 5C and 5D are advanced variants of the MQB49 immobilizer system used in recent Volkswagen Group vehicles, distinguished by specific dashboard and key types that require direct original key data reading for spare key addition. These systems represent the latest evolution in VAG security.', 2021, NULL, '', '[]', '["Golf Mk8", "Audi A3 (8Y)", "Skoda Octavia (NX)", "Seat Leon (KL)", "VW ID series (selected)"]', '[]', '{}', '', '', NULL, '["5C/5D", "VAG 5C", "VAG 5D", "MQB49 5C", "MQB49 5D"]', '', 'http://www.key-programmer.org/2026/01/20/difference-between-vag-mqb48-mqb49-5c-and-5d');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('MQB48', 'VAG MQB48 Immobilizer', '', 'immobilizer', '', 'MQB48 is an immobilizer system used in Volkswagen Group vehicles on the MQB platform, employing Megamos AES-48 encrypted transponders for enhanced security. It is common in mid-2010s to early 2020s models and often requires licensed tools or adapters for key programming.', 2014, 2022, '', '[]', '["Golf Mk7", "Audi A3 (8V)", "Skoda Octavia (5E)", "Seat Leon (5F)", "VW Tiguan (AD1)"]', '[]', '{}', '', '', NULL, '["MQB 48", "MQB48VT", "MQB48 GL"]', '', 'http://www.key-programmer.org/2026/01/20/difference-between-vag-mqb48-mqb49-5c-and-5d');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('XT57B', 'Xhorse XT57B Super Chip', '', 'tool', '', 'The XT57B is Xhorse''s latest universal reusable super transponder chip, capable of emulating a wide range of transponder types including 7935, 46, 47, 48, 49, 4A, and MQB48 GL. It is designed for use with Xhorse key programming devices and offers expanded compatibility over previous super chips.', NULL, NULL, '', '[]', '[]', '[]', '{}', '', '', NULL, '["Xhorse Super Chip XT57B", "VVDI Super Chip"]', '', 'https://www.bestkeysupply.com/products/xhorse-vvdi-super-chip-xt57b-chip-c27');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('754J Smart Key', 'Xhorse 754J Smart Key', '', 'tool', '', 'The 754J is an Xhorse smart key PCB for Audi BCM2 systems, supporting switchable frequencies (315/433/868 MHz) via software with no welding required. It is used to generate replacement or additional keys for 2013-2019 Audi models.', 2013, 2019, '', '[]', '["A4L", "A5", "A6L", "A8L", "Q5"]', '[]', '{}', '', '', NULL, '["XSADJ1GL", "Audi BCM2 Smart Key"]', '', 'https://www.xhorsevvdi.com/wholesale/xsadj1gl-smart-key-for-audi.html');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('8-in-1 Clip Adapters', 'Xhorse 8-in-1 Solder-free Clip Adapters', '', 'tool', '', 'The 8-in-1 clip adapters are universal solder-free interface boards that enable key data reading for BMW BDC3 (encrypted/unencrypted), BMW BCP, and VAG MQB49 5C/5D systems. They connect directly to key programmers like VVDI Key Tool Plus for bench key addition without soldering.', NULL, NULL, '', '[]', '[]', '[]', '{}', '', '', NULL, '["Universal BMW BDC3 VAG 5C/5D adapters", "8-in-1 adapters"]', '', 'https://www.obdii365.com/wholesale/universal-bmw-bdc3-vw-5c-5d-clip-adapters.html');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('Abrites MZ001', 'Abrites MZ001 License', '', 'tool', '', 'Abrites MZ001 is a special function license for the AVDI diagnostic system enabling key programming on 2020+ Mazda3 and CX-30 models. It supports both add-key and all-keys-lost scenarios for smart proximity keys.', 2020, NULL, '', '[]', '["Mazda3", "CX-30"]', '[]', '{}', '', '', NULL, '["AVDI MZ001"]', '', 'https://www.locksmithkeyless.com/products/abrites-avdi-mz001-key-programming-for-mazda-3-cx-30-2020');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('OBDStar DP Plus', 'OBDStar X300 DP Plus', '', 'tool', '', 'OBDStar X300 DP Plus (Key Master) is an all-in-one programmer supporting full immobilizer and key programming on 2020+ Mazda3 and CX-30, including all-keys-lost with PIN-free options in some cases.', 2020, NULL, '', '[]', '["Mazda3", "CX-30"]', '[]', '{}', '', '', NULL, '["OBDStar Key Master DP Plus", "OBDStar X300 Mini"]', '', 'https://royalkeysupply.com/products/obdstar-x300-mini-ford-mazda-key-programmer');
INSERT OR IGNORE INTO architecture_glossary (term, display_name, full_name, category, manufacturer, description, year_start, year_end, encryption, chip_types, models, chassis_codes, tool_support, security_level, security_notes, akl_capable, aliases, notes, source) VALUES ('VVDI MLB Tool', 'Xhorse VVDI MLB Tool', '', 'tool', '', 'The Xhorse VVDI MLB Tool is an aftermarket key programming device designed for Volkswagen Group MLB-platform vehicles, including Porsche models on MLB Evo. It supports wireless dealer key generation, immobilizer data reading, and SFD unlocking (with tokens) when paired with VVDI Key Tool Plus or VVDI2.', 2023, NULL, '', '[]', '["Cayenne (9YA)", "Macan (95B partial)", "other MLB Evo VAG models"]', '[]', '{}', '', '', NULL, '["Xhorse MLB Adapter", "VVDI MLB Adapter"]', '', 'https://abkeys.com/products/xhorse-vvdi-mlb-tool-to-add-key-for-vw-audi-porsche-vag-group-xdmlb0en');